    public static List<TableAndHql> buildHql(String entname, String pripid) {
        List<TableAndHql> tableAndHqlList = new ArrayList<>(81);

        // 通过pripid关联的信息
        List<String> tableList = Arrays.asList("e_lp_hstname", "e_inv_person", "e_inv_investment", "e_pri_person", "e_brchinfo", "e_alter_recoder", "e_revoke", "e_cancel", "e_sc_notice", "e_liq_mbr"
                // ------------------- 股权冻结
                , "e_sf_partyinfo", "e_sf_info", "e_sf_alter"
                // -------------------股权出质
                , "e_sp_pledge"
                // -------------------动产抵押
                , "mort_reg_info", "mort_person_info"
                // -------------------公示案件
                , "case_pub_partyinfo"
                // -------------------抽检抽查
                , "e_pub_spotcheck"
                // -------------------经营异常名录
                , "ao_opanomaly", "ao_opa_detail"
                // -------------------年报
                , "an_baseinfo"
                // -------------------企业公示即时信息
                , "e_im_permit", "e_im_investment", "e_im_invupdate", "e_im_ippldg", "e_im_case", "e_im_invsralt"
                // -------------------其他部门公示信息
                , "e_ot_permit", "e_ot_case"
                // -------------------严重违法失信企业信息
                , "e_li_illdishonesty", "e_li_illdisdetail"
                // -------------------公示公告
        );
        for (String table : tableList) {
            tableAndHqlList.add(new TableAndHql(table, "select * from " + table + " where pripid= '" + pripid + "'"));
        }
        return tableAndHqlList;
    }

    public static class TableAndHql {
        private String tableName;
        private String hql;

        public TableAndHql(String tableName, String hql) {
            this.tableName = tableName;
            this.hql = hql;
        }


        public String getTableName() {
            return tableName;
        }

        public void setTableName(String tableName) {
            this.tableName = tableName;
        }

        public String getHql() {
            return hql;
        }

        public void setHql(String hql) {
            this.hql = hql;
        }
    }
